bigsparql:
SELECT ?item ?itemLabel ?coord ?commons ?website ?img ?lang
            WHERE {
                ?item wdt:P31/wdt:P279* wd:Q33506 .
                ?item wdt:P17 ?paesi .
                VALUES ?paesi { wd:Q39 wd:Q183 wd:Q40 } .
                ?item wdt:P625 ?coord
                OPTIONAL { ?item wdt:P373 ?commons }
                OPTIONAL { ?item wdt:P856 ?website }
                OPTIONAL { ?item wdt:P18 ?img }
                OPTIONAL {?lang schema:about ?item .}
                SERVICE wikibase:label { bd:serviceParam wikibase:language "en,de,fr,it". }
            }
            ORDER BY ?item

sparql:
SELECT ?item ?itemLabel ?coord ?commons ?website ?img ?lang
            WHERE {
                ?item wdt:P31/wdt:P279* wd:Q33506 .
                ?item wdt:P17 wd:Q39 .
                ?item wdt:P625 ?coord
                OPTIONAL { ?item wdt:P373 ?commons }
                OPTIONAL { ?item wdt:P856 ?website }
                OPTIONAL { ?item wdt:P18 ?img }
                OPTIONAL {?lang schema:about ?item .}
                SERVICE wikibase:label { bd:serviceParam wikibase:language "en,de,fr,it". }
            }
            ORDER BY ?item
